enum class ArmorType {
	Immaterial,
	Swift,
	Natural,
	Arcane,
	Fortified,
	Illegal
}
enum class AttackType {
	Pierce,
	Impact,
	Magic,
	Siege,
	Pure,
	Illegal
}
enum class AttackMode {
	None,
	Melee,
	Ranged,
	Illegal
}
enum class UnitClass {
	Fighter,
	Creature,
	Mercenary,
	None,
	King,
	Worker,
	Illegal
}
enum class Legion {
	Element,
	Mech,
	Grove,
	Forsaken,
	Creature,
	Nether,
	Aspect,
	Illegal
}
